class CollegeSearch extends React.Component {
  //Initializes state and binds methods to component.
  constructor(props) {
    super(props);

    this.state = {
      currentEntry: '',
      names: [],
    };

    this.handleKeyPress = this.handleKeyPress.bind(this);
    this.handleElementClick = this.handleElementClick.bind(this);
    this.updateValue = this.updateValue.bind(this);
  }

  //Queries database/handles searches
  handleKeyPress(event) {
    //Constructs the URL to query from.
    var query = event.target.value;
    var api_format = "http://universities.hipolabs.com/search?name="
    api_format = api_format.concat(query, "&country=United+States");

    if (query === "") {
      this.setState({ names: [] });
      return;
    }
    //Gets JSON data from the API.
    fetch(api_format)
      .then((res) => res.json())
      .then((result) => {
        var len = (result.length > 7 ? 8 : result.length);
        var universities = [];
        for (var i = 0; i < len; i++) {
          if (universities.includes(result[i].name)) {
            continue;
          }
          else {
            universities.push(result[i].name);
          }
        }
        this.setState({ currentEntry: this.state.currentEntry, names: universities});
      });
  }

  //Updates the input in response to user queries
  updateValue(event) {
    this.setState({ currentEntry: event.target.value, names: this.state.names});
  }

  //Processes clicking on one of the searched features
  handleElementClick(event) {
      var val = event.target.innerHTML;
      this.setState({ currentEntry: val, names: [val] });
  }

  render() {
    var search_names = [];
    for (var i = 0; i < this.state.names.length; i++) {
      search_names.push(<div className='search_result' key={this.state.names[i]} onClick={this.handleElementClick}><p>{this.state.names[i]}</p></div>);
    }
    return (
      <form autoComplete='off'>
        <div className='autocomplete'>
          <input id="collegeSearch" type="text" name="college" value={this.state.currentEntry} placeholder='Enter a University'
            onKeyUp={this.handleKeyPress} onChange={this.updateValue}></input>
        </div>
        <div className='search_wrapper'>
          {search_names}
        </div>
      </form>
    );
  }
}

function App() {
  return (
    <div>
      <CollegeSearch />
    </div>
  );
}

export default App;
