import React, { useState } from "react";
import ProfileGrad from './ProfileGrad'

const Input = () => {
    return <ProfileGrad />;
  };

const Create = ({nextStep, prevStep}) => {

    const {
        undergrad_name,
        under_GPA,
        under_Major,
        under_Minor,
        grad_Name,
        grad_GPA,
        grad_Major,
        grad_Area,
        program_Name,
        description,
        ethnicity,
        gender,
        first_gen,
        citizenship,
        uni_applied,
        program_name,
        funding,
        app_result,
        app_decision
    } = values;
    const [ProfileGradList, setProfileGrad] = useState([]);
    const onAddBtnClick = event => {
        setProfileGrad(ProfileGradList.concat(<Input key={ProfileGradList.length} />));
    };
    const Continue = e => {
        e.preventDefault();
        nextStep();
    };

    const Previous = e => {
        e.preventDefault();
        prevStep();
    };

    return(
        <section className="profile_grad_all profile_section_all">
            <h4>Graduate Education</h4>
            <ProfileGrad />
            {ProfileGradList}
            <button onClick={onAddBtnClick}>+ Add another Grad Univeristy</button>
            <button onClick={Continue}>Next</button>
            <button onClick={Previous}>Previous</button>
        </section>
    ); 

}

export default Create;
