import React, { Component, useState } from "react";
import ReactDOM from "react-dom";
import ProfileUndergrad from './ProfileUndergrad/ProfileUndergrad'

const Input = () => {
    return <ProfileUndergrad />;
  };

const Create = () => {
    const [ProfileUndergradList, setProfileUndergrad] = useState([]);
    const onAddBtnClick = event => {
        setProfileUndergrad(ProfileUndergradList.concat(<Input key={ProfileUndergradList.length} />));
    };

    return(
        <section className="profile_submit_form">
            <h3>Profile Form</h3>
            <h4>Please Fill Out Your Past Application</h4>

            <section className="profile_from_all">
                <p className="profile_form_privacy_alert">***ALL FIELDS ARE OPTIONAL EXPECT APPLICATION RESULTS***</p>
                <p>Please only inlcude information when you apply to your PhD Programs</p>
            </section>

            <ProfileUndergrad />
            {ProfileUndergradList}
            <button onClick={onAddBtnClick}>+ Add another Undergrad Univeristy</button>


        </section>
    ); 

}

export default Create;
