import React, { useState } from "react";
import ProfileHeader from './ProfileHeader'
import './ProfileForm.css'

import ProfileUndergradAll from './ProfileUndergrad/ProfileUndergradAll'
import ProfileGradAll from './ProfileGrad/ProfileGradAll'
import ProfileUploadAll from './ProfileUpload/ProfileUploadAll'
import ProfileDemo from './ProfileDemo/ProfileDemoAll'
import ProfileRec from './ProfileRec/ProfileRecAll'
import ProfileResults from './ProfileResults/ProfileResultAll'


const Create = () =>  {

    let state = {
        undergrad_name: '',
        under_GPA: '',
        under_Major: '',
        under_Minor: '',
        grad_Name: '',
        grad_GPA: '',
        grad_Major: '',
        grad_Area: '',
        program_Name: '',
        description: '',
        ethnicity: '',
        gender: '',
        first_gen: '',
        citizenship:'',
        uni_applied:'',
        program_name:'',
        funding:'',
        app_result:'',
        app_decision:''
    }

    const [page, setPage] = useState(0);

    const handleChange = input => e =>{
        this.setState({[input]:e.target.value});
    }

    const componentList = [
        <ProfileUndergradAll 
        page={page}
        setPage={setPage}
        handleChange = {this.handleChange}
        values = {values}
        />,

        <ProfileGradAll 
        page={page}
        setPage={setPage}
        handleChange = {this.handleChange}
        values = {values}
        />,

        <ProfileRec
        page={page}
        setPage={setPage}
        handleChange = {this.handleChange}
        values = {values}
        />,

        <ProfileDemo
        page={page}
        setPage={setPage}
        handleChange = {this.handleChange}
        values = {values}
        />,

        <ProfileResults
        page={page}
        setPage={setPage}
        handleChange = {this.handleChange}
        values = {values}
        />,


        <ProfileUploadAll
        page={page}
        setPage={setPage}
        handleChange = {this.handleChange}
        values = {values}
        />
    ]

    const {
        undergrad_name,
        under_GPA,
        under_Major,
        under_Minor,
        grad_Name,
        grad_GPA,
        grad_Major,
        grad_Area,
        program_Name,
        description,
        ethnicity,
        gender,
        first_gen,
        citizenship,
        uni_applied,
        program_name,
        funding,
        app_result,
        app_decision
    } = this.state;

    const values = {
        undergrad_name,
        under_GPA,
        under_Major,
        under_Minor,
        grad_Name,
        grad_GPA,
        grad_Major,
        grad_Area,
        program_Name,
        description,
        ethnicity,
        gender,
        first_gen,
        citizenship,
        uni_applied,
        program_name,
        funding,
        app_result,
        app_decision
    }

    return(
        <section className="ProfileFormAll">
            <ProfileHeader/>
            <section className="profile_content_all">{componentList[page]}</section>
        </section>
    )
}

export default Create;
