import {Control, useFieldArray, useForm, useWatch} from "react-hook-form"

let renderCount = 0;

// type FormValues = {
//     cart:{
//         name: String,
//         amount: Number,
//     }[]
// }

function ProfileFormAll() {
    const { register, formState: {errors}, control } = useForm({
        defaultValues : {
            cart : [{name:'', amount: 0}]
        }
    });
    const {fields, append, remove} = useFieldArray({
        name: 'cart',
        control
    })
    renderCount++;

    const handleSubmit = event => {
        // üëáÔ∏è prevent page refresh
        event.preventDefault();

        console.log(event);
      };

    return(
        <div>
            <form onSubmit={handleSubmit}>
            {fields.map((field, index) =>{
                return <section key={field.id}>
                    <input {...register('cart${index}.name', { required: true })}/>
                    <input type = "number" {...register('cart${index}.amount')}/>
                    <button type="button" onClick={() => remove(index)}>Delete</button>
                </section>
            }

            )}

            <button type = "button" 
            onClick={()=>{
                append({
                    name:'',
                    amount: 0,
                });
            }}>Append</button>

            <button type="submit">Submit</button>
            </form>
        </div>
    )

}

export default ProfileFormAll;
