import { useState } from "react";

export function ProfileFormAll(){
    const [hobby, setHobby] = useState([''])

  const handleChange = (value, index) => {
    const newHobby = hobby.map((hobbyItem, hobbyIndex) => {
      return hobbyIndex === index ? value : hobbyItem
    })
    setHobby(newHobby)

  }

  return (
    <div>
      Hobbies :
      <button
        onClick={() => {
          setHobby([...hobby, ''])
        }}
      >
        ADD
      </button>
      {
        hobby.map((item, index) => {
          return <div style={{ display: 'flex' }}>
            <input value={item} onChange={(e) => handleChange(e.target.value, index)} />
            <button
              onClick={() => {
                const newarr = hobby.filter((i, j) => {
                  return index !== j
                })
                console.log(newarr)
                setHobby(newarr)
              }}
            >
              DELETE
            </button>
          </div>
        })
      }
    </div>
  );
}

export default ProfileFormAll
