import {Control, get, useFieldArray, useForm, useWatch} from "react-hook-form"

let renderCount = 0;

// type FormValues = {
//     cart:{
//         name: String,
//         amount: Number,
//     }[]
// }

export function ProfileFormAll() {
    const { register, formState: {errors}, control, getValues } = useForm({
        defaultValues : {
            cart : [{name:'', amount: 0}],
            test : [{id:'', quant: 0}]
        }
    });
    const {fields, append, remove} = useFieldArray({
        name: 'cart',
        control
    })
    renderCount++;



    return(
        <div>
            <form onClick={() => {(console.log(getValues()))}}>
            {fields.map((field, index) =>{
                return <section key={field.id}>
                    <input {...register('cart${index}.name', { required: true })}/>
                    <input type = "number" {...register('cart${index}.amount')}/>
                    <button type="button" onClick={() => remove(index)}>Delete</button>
                </section>
            }
            )}

            <button type = "button" 
            onClick={()=>{
                append({
                    name:'',
                    amount: 0,
                });
            }}>Append</button>

            <button type="submit">Submit</button>
            </form>
        </div>
    )
}
